import React ,{useState} from "react";

import './ExpenseForm.css'

export const ExpenseForm = () => {

    const [newexpenses,setnewexpenses] = useState({
        Title :'',
        Amount :0,
        Date : null
    })

    const titleChangeHandler = (event) => {
        setnewexpenses(newexpenses[0].Title=event.target.value)

    }
    
    const amountChangeHandler = (event) => {
        console.log(event.target.value)
        setnewexpenses(...newexpenses,
            newexpenses.Amount=event.target.value)

    }

    const dateChangeHandler = (event) => {
        console.log(event.target.value)
        setnewexpenses(...newexpenses,
            newexpenses.Date=event.target.value)

    }

    const submitHandler = () => {
        console.log(newexpenses)
    }

    return <form >
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input onChange={titleChangeHandler} type='text'></input>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input onChange={amountChangeHandler} type='number' min='0.01' step='0.01'></input>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input onChange={dateChangeHandler} type='date' min='2019-01-01' max='2023-12-31'></input>
                </div>
            </div>
            <div className='new-expense__actions'>
                <button onClick={submitHandler} type='submit'>Add Expense</button>
            </div>

    </form>
}

export default ExpenseForm;
